import React, { useEffect, useState } from 'react';
import { SuperHeros } from './SuperHeros'; // Importation de notre classe SuperHeros
import SuperHerosData from './SuperHeros.json'; // Importation du fichier JSON

export const App = () => {
  const [heroes, setHeroes] = useState<SuperHeros[]>([]);
  const [searchText, setSearchText] = useState<string>("");
  const [sortKey, setSortKey] = useState<string>("name");
  useEffect(() => {
    const heroesFromData = SuperHerosData.map(
      (heroData: any) => new SuperHeros(heroData.id, heroData.name, heroData['id-api'], heroData.slug)
    );
    setHeroes(heroesFromData);
  }, []);

  const filteredHeroes = heroes
    .filter(hero => hero.name.toLowerCase().includes(searchText.toLowerCase()))
    .sort((a, b) => {
      if (sortKey === "name") {
        return a.name.localeCompare(b.name);
      }
      return 0; // Ajouter d'autres critères de tri si nécessaire
    });

    const sortedHeroes = filteredHeroes.sort((a: SuperHeros, b: SuperHeros) => {
      if (sortKey === "name") {
        return a.name.localeCompare(b.name);
      } else {
        return a.id - b.id; // Ajout d'un tri par ID si le critère est "id"
      }
    });

  return (
    <div>
      <h1>Super Heroes App</h1>
      <p>Nombre de super-héros chargés: {heroes.length}</p>
      <input 
        type="text" 
        placeholder="Rechercher un super-héros..." 
        value={searchText} 
        onChange={(e) => setSearchText(e.target.value)} 
      />

      <select value={sortKey} onChange={(e) => setSortKey(e.target.value)}>
        <option value="name">Nom</option>
        <option value="id">ID</option>
        {/* Ajouter d'autres options de tri ici si nécessaire */}
      </select>

      {sortedHeroes.map((hero: SuperHeros) => (
        <div key={hero.id}>
          <h2>{hero.name}</h2>
          <p>Id: {hero.id}</p>
          <p>Id API: {hero.idApi}</p>
          <p>Slug: {hero.slug}</p>
          <img 
            src={`https://cdn.jsdelivr.net/gh/rtomczak/superhero-api@0.3.0/api/images/sm/${hero.slug}.jpg`} 
            alt={hero.name} 
          />
        </div>
      ))}
    </div>
  );
};

export default App;
