import React, { FunctionComponent, useState } from 'react';

const App: FunctionComponent = () => {
    const [name, setName] = useState<string>('React !');

    const addExclamationMark = () => {
        setName(prevName => prevName + '!');
    };

    const resetName = () => {
        setName('React');
    };

    return (
        <div>
            <h1>Avec React : Hello {name}</h1>
            <button onClick={addExclamationMark}>Ajouter un point d'exclamation</button>
            <button onClick={resetName}>Réinitialiser</button>
        </div>
        
    )
}

export default App;
